var popup=document.querySelector(".popup"),popupOpen=document.querySelector(".adress__button"),popupClose=popup.querySelector(".write-us__close"),sliderToggler=document.querySelector(".slider-controls-group"),firstSlide=document.querySelector(".slider__item-1"),secondSlide=document.querySelector(".slider__item-2"),thirdSlide=document.querySelector(".slider__item-3");popupOpen.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("popup-show")}),popupClose.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("popup-show")}),sliderToggler.addEventListener("click",function(){document.slider.slider__controls[0].checked?(firstSlide.classList.add("slider-show"),secondSlide.classList.remove("slider-show"),thirdSlide.classList.remove("slider-show")):document.slider.slider__controls[1].checked?(secondSlide.classList.add("slider-show"),firstSlide.classList.remove("slider-show"),thirdSlide.classList.remove("slider-show")):document.slider.slider__controls[2].checked&&(thirdSlide.classList.add("slider-show"),firstSlide.classList.remove("slider-show"),secondSlide.classList.remove("slider-show"))});